import React from 'react';
import 'antd/dist/antd.css';
import {
    Drawer, Form, Button, Col, Row, Input, Select, DatePicker, Icon,
} from 'antd';
import reqwest from 'reqwest';


const {Option} = Select;

class DrawerForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            visible: false,
            name: '',
            gender: '',
            birthday: '',
            department: '',
            level: ''
        };
    }


    showDrawer = () => {
        this.setState({
            visible: true,
        });
    };

    onClose = () => {
        this.setState({
            visible: false,
        });
    };

    handleChangeGender(value) {
        this.setState({
            gender: value,
        });
        console.log(this.state.gender)
    }

    handleChangeDepartment(value) {
        this.setState({
            department: value,
        });
        console.log(this.state.department)
    }

    handleChangeLevel(value) {
        this.setState({
            level: value,
        });
        console.log(this.state.level)
    }

    handleChangeBirthday(value) {
        this.setState({
            birthday: value,
        });
        console.log(this.state.birthday)
    }

    handleChangeName(value) {
        this.setState({
            name: value,
        });
        console.log(this.state.name)
    }

    render() {
        const {getFieldDecorator} = this.props.form;
        return (
            <div>
                <Button type="primary" onClick={this.showDrawer}>
                    <Icon type="plus"/> New account
                </Button>
                <Drawer
                    title="添加员工"
                    width={300}
                    onClose={this.onClose}
                    visible={this.state.visible}
                >
                    <Form layout="vertical" hideRequiredMark>
                        <Row gutter={16}>
                            <Col span={20}>
                                <Form.Item label="姓名">
                                    <Input onChange={this.handleChangeName.bind(this)} placeholder="请输入姓名"/>
                                </Form.Item>
                            </Col>
                        </Row>
                        <Row gutter={16}>
                            <Col span={12}>
                                <Form.Item label="性别">
                                    <Select placeholder="请选择性别" onSelect={this.handleChangeGender.bind(this)}>
                                        <Option value="0">男</Option>
                                        <Option value="1">女</Option>
                                    </Select>
                                </Form.Item>
                            </Col>
                        </Row>
                        <Row gutter={16}>
                            <Col span={20}>
                                <Form.Item label="出生日期">
                                    <DatePicker onChange={this.handleChangeBirthday.bind(this)} placeholder="请选择生日"/>
                                </Form.Item>
                            </Col>
                        </Row>
                        <Row gutter={16}>
                            <Col span={12}>
                                <Form.Item label="部门">
                                    <Select placeholder="请选择部门" onChange={this.handleChangeDepartment.bind(this)}>
                                        <Option value="1">产品部</Option>
                                        <Option value="2">技术部</Option>
                                        <Option value="3">行政部</Option>
                                        <Option value="4">运营部</Option>
                                        <Option value="5">采购部</Option>
                                    </Select>
                                </Form.Item>
                            </Col>
                        </Row>
                        <Row gutter={16}>
                            <Col span={12}>
                                <Form.Item label="职位">
                                    <Select placeholder="请选择职位" onChange={this.handleChangeLevel.bind(this)}>
                                        <Option value="1">普通职员</Option>
                                        <Option value="2">组长</Option>
                                        <Option value="3">经理</Option>
                                        <Option value="4">总监</Option>
                                        <Option value="5">董事长</Option>
                                    </Select>
                                </Form.Item>
                            </Col>
                        </Row>
                    </Form>
                    <div
                        style={{
                            position: 'absolute',
                            left: 0,
                            bottom: 0,
                            width: '100%',
                            borderTop: '1px solid #e9e9e9',
                            padding: '10px 16px',
                            background: '#fff',
                            textAlign: 'right',
                        }}
                    >
                        <Button onClick={this.onClose} style={{marginRight: 8}}>
                            取消
                        </Button>
                        <Button onClick={this.onClose} type="primary">
                            确定
                        </Button>
                    </div>
                </Drawer>
            </div>
        );
    }
}

export default Form.create()(DrawerForm);
