import React from 'react';
import 'antd/dist/antd.css';
import {
    Form, Input, Drawer, Icon, Select, DatePicker, Col, Checkbox, Button, AutoComplete,
} from 'antd';

const {Option} = Select;

class RegistrationForm extends React.Component {
    state = {
        visible: false,
    };

    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
        });
    };

    showDrawer = () => {
        this.setState({
            visible: true,
        });
    };

    onClose = () => {
        this.setState({
            visible: false,
        });
    };

    render() {
        const {getFieldDecorator} = this.props.form;

        return (
            <div>
                <Button type="primary" onClick={this.showDrawer}>
                    <Icon type="plus"/>添加员工
                </Button>
                <Drawer
                    title="添加员工"
                    width={300}
                    onClose={this.onClose}
                    visible={this.state.visible}
                >
                    <Form onSubmit={this.handleSubmit}>
                        <Form.Item
                            label="E-mail"
                        >
                            {getFieldDecorator('email', {
                                rules: [{
                                    type: 'email', message: 'The input is not valid E-mail!',
                                }, {
                                    required: true, message: 'Please input your E-mail!',
                                }],
                            })(
                                <Input/>
                            )}
                        </Form.Item>
                        <Form.Item
                            label="姓名"
                        >
                            {getFieldDecorator('name', {
                                rules: [{
                                    required: true, message: 'Please input your name!',
                                }],
                            })(
                                <Input/>
                            )}
                        </Form.Item>
                        <Form.Item label="性别">
                            {getFieldDecorator('gender', {
                                rules: [{
                                    type: 'gender', message: 'The input is not valid gender!',
                                }, {
                                    required: true, message: 'Please select your gender!',
                                }],
                            })(
                                <Select placeholder="请选择性别" onSelect={this.handleChangeGender.bind(this)}>
                                    <Option value="0">男</Option>
                                    <Option value="1">女</Option>
                                </Select>
                            )}
                        </Form.Item>
                        <Form.Item label="出生日期">
                            {getFieldDecorator('birthday', {
                                rules: [{
                                    type: 'birthday', message: 'The input is not valid gender!',
                                }, {
                                    required: true, message: 'Please select your gender!',
                                }],
                            })(
                                <DatePicker onChange={this.handleChangeBirthday.bind(this)} placeholder="请选择生日"/>
                            )}
                        </Form.Item>
                        <Form.Item label="部门">
                            {getFieldDecorator('department', {
                                rules: [{
                                    type: 'department', message: 'The input is not valid gender!',
                                }, {
                                    required: true, message: 'Please select your gender!',
                                }],
                            })(
                                <Select placeholder="请选择部门" onChange={this.handleChangeDepartment.bind(this)}>
                                    <Option value="1">产品部</Option>
                                    <Option value="2">技术部</Option>
                                    <Option value="3">行政部</Option>
                                    <Option value="4">运营部</Option>
                                    <Option value="5">采购部</Option>
                                </Select>
                            )}
                        </Form.Item>
                        <Form.Item label="职位">
                            {getFieldDecorator('level', {
                                rules: [{
                                    type: 'level', message: 'The input is not valid gender!',
                                }, {
                                    required: true, message: 'Please select your gender!',
                                }],
                            })(
                                <Select placeholder="请选择职位" onChange={this.handleChangeLevel.bind(this)}>
                                    <Option value="1">普通职员</Option>
                                    <Option value="2">组长</Option>
                                    <Option value="3">经理</Option>
                                    <Option value="4">总监</Option>
                                    <Option value="5">董事长</Option>
                                </Select>
                            )}
                        </Form.Item>
                        <Form.Item>
                            <Button type="primary" htmlType="submit">添加</Button>
                        </Form.Item>
                    </Form>
                </Drawer>
            </div>
        );
    }
}

const WrappedRegistrationForm = Form.create({name: 'register'})(RegistrationForm);

export default WrappedRegistrationForm;
