import React, {Component} from 'react';
import {Button, Icon, Input, Table} from 'antd';
import reqwest from 'reqwest';

const columns = [{
    title: '姓名',
    dataIndex: 'name',
    sorter: true,
    render: name => `${name}`,
    width: '20%',
}, {
    title: '年龄',
    dataIndex: 'age',
    sorter: true,
    width: '20%'
}, {
    title: '性别',
    dataIndex: 'gender',
    filters: [
        {text: 'Male', value: '0'},
        {text: 'Female', value: '1'},
    ],
    width: '20%',
}, {
    title: '部门',
    dataIndex: 'department',
    filters: [
        {text: '产品部', value: '1'},
        {text: '技术部', value: '2'},
        {text: '运营部', value: '3'},
        {text: '行政部', value: '4'},
        {text: '采购部', value: '5'},
    ],
    width: '20%',
}, {
    title: '职位',
    dataIndex: 'level',
    filters: [
        {text: '普通职员', value: '1'},
        {text: '组长', value: '2'},
        {text: '经理', value: '3'},
        {text: '总监', value: '4'},
        {text: '董事长', value: '5'},
    ],
    width: '20%',
}];

class MyTable extends Component {

    static searchUrl = 'http://localhost:8888/employee';

    constructor(props) {
        super(props);
        this.state = {
            data: [],
            pagination: {},
            loading: false,
        };
    };

    componentDidMount() {
        this.fetch();
    }

    fetch = (params = {}) => {
        console.log('params:', params);
        this.setState({loading: true});
        reqwest({
            url: MyTable.searchUrl,
            method: 'get',
            data: {
                results: 10,
                ...params,
            },
            type: 'json',
        }).then((data) => {
            const pagination = {...this.state.pagination};
            // Read total count from server
            // pagination.total = data.totalCount;
            pagination.total = data.data.count;
            this.setState({
                loading: false,
                data: data.data.results,
                pagination,
            });
        });
        this.setState({
            loading: false
        })
    };

    handleTableChange = (pagination, filters, sorter) => {
        const pager = {...this.state.pagination};
        pager.current = pagination.current;
        this.setState({
            pagination: pager,
        });
        this.fetch({
            results: pagination.pageSize,
            page: pagination.current,
            sortField: sorter.field,
            sortOrder: sorter.order,
            ...filters,
        });
    };

    render() {
        return (
            <Table
                columns={columns}
                rowKey={record => record.id}
                dataSource={this.state.data}
                pagination={this.state.pagination}
                loading={this.state.loading}
                onChange={this.handleTableChange}
            />
        )
    };
}

export default MyTable;
